        /**
         * @group model
         */
        public function testRelations()
        {
                $file = File::findFirst();
                self::assertNotNull($file);

                self::assertNotEquals($file->answer->count(), 0);
                self::assertTrue(count($file->answer) == 1);
        }

        /**
