                $values = array(
                        'answer_id' => Answer::findFirst()->id,
                        'name'      => 'Name1',
                        'path'      => '/tmp/path',
                        'type'      => 'video',
                        'subtype'   => 'mp4'
                );

                try {
                        $helper = new TestModel(new File());
                        $helper->tryPersist();
                        self::fail("Excepted constraint violation exception");
                } catch (Exception $exception) {
                        // Expected exception
                }

                try {
                        $helper = new TestModel(new File());
                        $helper->tryPersist($values);
                } catch (Exception $exception) {
                        self::fail($exception);
                }

                $values = array(
                        'answer_id'    => Answer::findFirst()->id,
                        'name'         => 'Name1',
                        'path'         => '/tmp/path',
                        'type'         => 'video',
                        'subtype'      => 'mp4',
                        'non_existing' => 666   // ignored wihout error
                );
                try {
                        $helper = new TestModel(new File());
                        $helper->tryPersist($values);
                } catch (Exception $exception) {
                        self::fail("Unexcepted constraint violation exception");
                }

                // 
                // Test shared property:
                // 
                $media = new File();
                $media->assign($values);
                $media->save();
        }

